#include <sys/types.h>
#include <sys/mount.h>
#include <sys/mman.h>
#include <sys/stat.h>
#include <sys/xattr.h>
#include <sys/syscall.h>

#include <dirent.h>
#include <errno.h>
#include <error.h>
#include <fcntl.h>
#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <unistd.h>

int main(int argc, char *argv[])
{
    if (argc < 2){
        printf("usage: %s path_to_mounted_dir\n", argv[0]);
        return 0;
    }
    chdir(argv[1]);

    mkdir("A", 755);
    mkdir("A/X", 755);
    int fd0 = creat("A/X/foo", O_RDWR);
    int fd1 = creat("A/X/bar", O_RDWR);
    write(fd0, "", 8192);
    write(fd1, "", 8192);
    sync();
    close(fd0);
    close(fd1);

    rename("A/X", "A/Y");
    mkdir("A/X", 755);
    DIR* ds = opendir("A/X");
    int fd2 = dirfd(ds);
    fsync(fd2);

    system("echo b > /proc/sysrq-trigger");
    return 0;
}
